mapper.select:
	    select * from xunjia.t_askpricepici_total
	   
	     1=1
	    
				
					AND DATE_FORMAT(createOn,'%Y-%m-%d') >= #{params.beginOn}
				
			
			
				
					AND DATE_FORMAT(createOn,'%Y-%m-%d') <= #{params.endOn}
				
			
			
				
					AND DATE_FORMAT(createOn,'%Y-%m') >= #{params.beginOnNew}
				
			
			
				
					AND DATE_FORMAT(createOn,'%Y-%m') <= #{params.endOnNew}
				
			
			 AND status = ${params.status}
			 AND piciId in(${params.ids})
		    
		       
		          and type=1
		          and fromType in(0)
		       
		       
		          and 1=1
		       
		       
		          and type=0
		          
		       		 
		              and fromType in(${params.fromType})
		             
		             
		                and fromType in(0,1)
		             
		          
		       
		    
		    
					and province = #{params.province}
		    
		    
		        	and city = #{params.city}
		    
		    
	        	    and ename like CONCAT('%',#{params.ename},'%')
	        
	        
	        	    and eid like CONCAT('%',#{params.account},'%')
	        
     	     AND piciId LIKE '%${params.piciId}%'
     	     AND proName LIKE '%${params.proName}%'
     	     AND engineer LIKE '%${params.engineer}%'
     	     AND corpName LIKE '%${params.corpName}%'
     	    
	   		 AND eid = #{params.eid}
	   		
	   		
			   		
			   		     
			   		       
			   		        AND DATE_FORMAT(createOn,'%Y')=#{params.dateStr}
			   		        
			   		     
			   		     
			   		     
			   		       AND DATE_FORMAT(createOn,'%Y-%m')=#{params.dateStr}
			   		       
			   		     
			   		     
			   		       
			   		           AND DATE_FORMAT(createOn,'%Y-%m-%d')=#{params.dateStr}
			   		       
			   		     
			   		
	   		
	   		 AND createBy = ${params.createBy}
	   
	     order by piciCreateOn desc

        select count(1) from xunjia.t_askpricepici_total
	   
	     1=1
	    
				
					AND DATE_FORMAT(createOn,'%Y-%m-%d') >= #{params.beginOn}
				
			
			
				
					AND DATE_FORMAT(createOn,'%Y-%m-%d') <= #{params.endOn}
				
			
			
				
					AND DATE_FORMAT(createOn,'%Y-%m') >= #{params.beginOnNew}
				
			
			
				
					AND DATE_FORMAT(createOn,'%Y-%m') <= #{params.endOnNew}
				
			
			 AND status = ${params.status}
		    
		       
		          and type=1
		          and fromType in(0)
		       
		       
		          and 1=1
		       
		       
		          and type=0
		          
		       		 
		              and fromType in(${params.fromType})
		             
		             
		                and fromType in(0,1)
		             
		          
		       
		    
		    
					and province = #{params.province}
		    
		    
		        	and city = #{params.city}
		    
		    
	        	    and ename like CONCAT('%',#{params.ename},'%')
	        
	        
	        	    and eid like CONCAT('%',#{params.account},'%')
	        
     	     AND piciId LIKE '%${params.piciId}%'
     	     AND proName LIKE '%${params.proName}%'
     	     AND engineer LIKE '%${params.engineer}%'
     	     AND corpName LIKE '%${params.corpName}%'
     	    
	   		 AND eid = #{params.eid}
	   		
		   		
		   		
		   		
			   		
			   		     
			   		       
			   		        AND DATE_FORMAT(createOn,'%Y')=#{params.dateStr}
			   		        
			   		     
			   		     
			   		     
			   		       AND DATE_FORMAT(createOn,'%Y-%m')=#{params.dateStr}
			   		       
			   		     
			   		     
			   		       
			   		           AND DATE_FORMAT(createOn,'%Y-%m-%d')=#{params.dateStr}
			   		       
			   		     
			   		
	   			
		   		
	   		
	   		 AND createBy = ${params.createBy}

	    select * from xunjia.tAskPricePici
	   
	   1=1
	    
				
					AND DATE_FORMAT(tAskPricePici.createOn,'%Y-%m-%d') >= #{params.beginOn}
				
			
			
				
					AND DATE_FORMAT(tAskPricePici.createOn,'%Y-%m-%d') <= #{params.endOn}
				
			
			 AND status = ${params.status}
			
		    
			      and (eid IS NULL or LENGTH(TRIM(eid))=0)
		    
     	     AND piciId LIKE '%${params.piciId}%'
     	     AND proName LIKE '%${params.proName}%'
     	     AND engineer LIKE '%${params.engineer}%'
     	     AND corpName LIKE '%${params.corpName}%'
     	    
	   		 AND eid = #{params.eid}
	   		 AND fromType = ${params.fromType}
	   		
		   		
		   		     AND DATE_FORMAT(createOn,'%Y')=#{params.dateStr}
		   		     AND DATE_FORMAT(createOn,'%Y-%m')=#{params.dateStr}
		   		     AND DATE_FORMAT(createOn,'%Y-%m-%d')=#{params.dateStr}
		   		
	   		
	   		 AND createBy = '${params.createBy}' 
	   		
	   		    AND (proName LIKE '%${params.keyword}%' OR engineer LIKE '%${params.keyword}%' OR summary LIKE '%${params.keyword}%')
	   		
	   		 AND isLock = ${params.isLock}
	   
	   	ORDER BY tAskPricePici.id DESC

    select count(1) from xunjia.tAskPricePici
    
   		1 = 1
   		 AND eid = #{params.eid}
   		 AND fromType = ${params.fromType}
   		
   		    AND (proName LIKE '%${params.keyword}%' OR engineer LIKE '%${params.keyword}%' OR summary LIKE '%${params.keyword}%')
   		
   		
	   		
	   		     AND DATE_FORMAT(createOn,'%Y')=#{params.dateStr}
	   		     AND DATE_FORMAT(createOn,'%Y-%m')=#{params.dateStr}
	   		     AND DATE_FORMAT(createOn,'%Y-%m-%d')=#{params.dateStr}
